mapper.insert:
INSERT INTO blog_user_share
(user_id, share_id, share_url, share_title, share_desc, create_time, update_time, record_count)
VALUES (#{userId},#{shareId},#{shareUrl},#{shareTitle},#{shareDesc},now(),now(),#{recordCount});

mapper.update:
UPDATE blog_user_share
SET state = '0'
WHERE user_id = #{userId}
AND share_id = #{shareId}
AND state = '1'
